package lord;

public class Summa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String [] x = {"sri","hari", "Ramu","Ramila"};
		int a =0;
		for (int i = 0; i <= x.length;i++) {
			int y = x[i].length();
		
		if ( a <= y) {
			a =y;
		}
		System.out.println(a);
		
	}
	}
}
